<span class="line">        <span class="token function">HFLogPrint</span><span class="token punctuation">(</span>HF_LOG_ERROR<span class="token punctuation">,</span> <span class="token string">&quot;Usage: %s &lt;pack_path&gt; &lt;source_path&gt; [rotation]&quot;</span><span class="token punctuation">,</span> argv<span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">]</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
